setup.pg-code:
do {
$b = random(2,6,1);
$a = random(3,7,1)*$b;
$c = random(1,7,1)*$b;
} until (($a/$c != 1) and (gcd($a,$c) !=$c));
$m1 = random(2,20,1);
do {$n1 = random(4,20,1)} until ($n1 != $m1);
do {$p1 = random(5,20,1)} until ($p1 != $m1 and $p1 != $n1);

Context("Numeric")->variables->are(x=>"Real");
$var = "x";

LimitedPowers::OnlyPositiveIntegers();
$frac = "\frac{\left($a $var^{$m1}\right)\left($var^{$n1}\right)}{$c $var^{-$p1}}";

#Context(LimitedPowers::OnlyPositiveIntegers());
$numconst = $a/gcd($a,$c);
$denconst = $c/gcd($a,$c);
$numexp1 = ($m1+$n1+$p1);
$den = Formula("$denconst ")->reduce;
$num = Formula("$numconst $var^{$numexp1}")->reduce;
Context()->texStrings;
if ($denconst == 1) {
  $texanswer = "\( $num  } \)";
} else {
  $texanswer = "\(\displaystyle \frac{$num }{$denconst  } \)";
}

$multians = MultiAnswer($num, $den)->with(
  singleResult => 0,
  allowBlankAnswers => 1,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $f1stu, $f2stu ) = @{$student};
      my ( $f1, $f2 ) = @{$correct};

      if ( ( $f1==$f1stu and  $f2==$f2stu) ) {
          return [1,1];
      } elsif ( $f1==$f1stu ) {
          return [1,0];
      } elsif ( $f2==$f2stu ) {
          return [0,1];
      } elsif ( $f1*$f2stu==$f1stu*$f2 ) {
          $self->setMessage(1,"Simplify your answer further");
          $self->setMessage(2,"Simplify your answer further");
          return [0,0];
      } else {
          return [0,0];
      }
  }
);
Context()->operators->undefine("+","-","/");

