setup.pg-code:
Context("Vector2D");
Context()->variables->are(t=>"Real");

$P = Point(1,2);
$Q = Point(-1,3);
$PQ = Vector(-2,1);

$t = Formula("t");
$line = Vector("$P + $t * $PQ");

$multians = MultiAnswer( $line, Real("0"), Real("1") )->with(
  singleResult => 0,
  checker => sub {
    my ( $correct, $student, $ansHash ) = @_;
    my ( $linestu, $astu, $bstu ) = @{$student};
    my ( $linecor, $acor, $bcor ) = @{$correct};

    if ( (ParametricLine("$line") == $linestu) and
         ($linestu->eval(t=>$astu) == $line->eval(t=>"0")) and
         ($linestu->eval(t=>$bstu) == $line->eval(t=>"1")) ) 
    {  
       return [1,1,1]; 

    } elsif ( (ParametricLine("$line") == $linestu) and
              ($linestu->eval(t=>$astu) == $line->eval(t=>"0")) )
    {  
       return [1,1,0]; 

    } elsif ( (ParametricLine("$line") == $linestu) and
              ($linestu->eval(t=>$bstu) == $line->eval(t=>"1")) )
    {  
       return [1,0,1]; 

    } elsif ( (ParametricLine("$line") == $linestu) )
    {
        return [1,0,0];

    } else { 
        return [0,0,0]; 
    }

  }
);

Context("Numeric");
Context()->variables->are(t=>"Real");
Context()->variables->set(t=>{limits=>[-5,5]});

$x = Formula("2*cos(t)+1");
$y = Formula("2*sin(t)-2");
$t0 = Compute("0");
$t1 = Compute("pi");

($x0,$y0) = (3,-2);
($x1,$y1) = (-1,-2);

$multians = MultiAnswer($x, $y, $t0, $t1)->with(
  singleResult => 0,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $xstu, $ystu, $t0stu, $t1stu ) = @{$student};
      if ( 
           ( (($xstu-1)**2 + ($ystu+2)**2) == 4 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) {
            return [1,1,1,1];

      } elsif (
           ( (($xstu-1)**2 + ($ystu+2)**2) == 4 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) 
         ) { 
            return [1,1,1,0];

      } elsif (
           ( (($xstu-1)**2 + ($ystu+2)**2) == 4 )  and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) { 
            return [1,1,0,1];

      } elsif (
           ( (($xstu-1)**2 + ($ystu+2)**2) == 4 ) 
         ) { 
            return [1,1,0,0];

      } else {
            return [0,0,0,0];
      }
  }
);
