Context("Vector2D");
Context()->variables->are(t=>"Real");

$x = Formula("t");
$y = Formula("2*t^2-1");


$vec = Vector("$x*i+$y*j");
$t0 = Compute("-1");
$t1 = Compute("2");

($x0,$y0) = (-1,1);
($x1,$y1) = (2,7);

$multians = MultiAnswer($vec, $t0, $t1)->with(
  singleResult => 0,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $vecstu, $t0stu, $t1stu ) = @{$student};
      $xstu=$vecstu.i;
      $ystu=$vecstu.j;
      if ( 
           ( (2*($xstu)**2-1-$ystu) == 0 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) {
            return [1,1,1];

      } elsif (
           ( (2*($xstu)**2-1-$ystu) == 0 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) 
         ) { 
            return [1,1,0];

      } elsif (
           ( (2*($xstu)**2-1-$ystu) == 0 )  and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) { 
            return [1,0,1];

      } elsif (
           ( (2*($xstu)**2-1-$ystu) == 0 ) 
         ) { 
            return [1,0,0];

      } else {
            return [0,0,0];
      }
  }
);

Context("Vector2D");
Context()->variables->are(t=>"Real");

$x = Formula("sin(t)");
$y = Formula("t");


$vec = Vector("$x*i+$y*j");
$t0 = Compute("0");
$t1 = Compute("2*pi");

($x0,$y0) = (0,0);
($x1,$y1) = (0,2*pi);

$multians = MultiAnswer($vec, $t0, $t1)->with(
  singleResult => 0,
  checker => sub {
      my ( $correct, $student, $self ) = @_;
      my ( $vecstu, $t0stu, $t1stu ) = @{$student};
      $xstu=$vecstu.i;
      $ystu=$vecstu.j;
      if ( 
           ( (sin($ystu)-$xstu) == 0 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) {
            return [1,1,1];

      } elsif (
           ( (sin($ystu)-$xstu) == 0 )  and
           ( ($xstu->eval(t=>$t0stu)) == $x0 ) and
           ( ($ystu->eval(t=>$t0stu)) == $y0 ) 
         ) { 
            return [1,1,0];

      } elsif (
           ( (sin($ystu)-$xstu) == 0 )  and
           ( ($xstu->eval(t=>$t1stu)) == $x1 ) and
           ( ($ystu->eval(t=>$t1stu)) == $y1 )
         ) { 
            return [1,0,1];

      } elsif (
           ( (sin($ystu)-$xstu) == 0 ) 
         ) { 
            return [1,0,0];

      } else {
            return [0,0,0];
      }
  }
);

