############################################################ # Load Macros ############################################################ loadMacros( "PGstandard.pl", "MathObjects.pl", "PGML.pl", "AnswerFormatHelp.pl", "parserPopUp.pl", "PGcourse.pl", );
############################################################ # Header ############################################################ COMMENT('Authored in PreTeXt'); TEXT(beginproblem());
############################################################ # PG Setup ############################################################ Context('Numeric'); $MC1= PopUp( ["?","positive.","negative.","zero.","there is not enough information to decide."], "negative." # correct answer );
############################################################ # Body ############################################################
BEGIN_PGML For the given vector field [`\vec{F}`] and curve [`C`] (oriented from left to right), determine whether [`\int_{C} \vec{F} \cdot d\vec{r}`] is positive, negative, or zero.
[`\int_{C} \vec{F} \cdot d\vec{r}`] is [_]{$MC1}{5}
END_PGML
############################################################ # Hint ############################################################ #Set value of $showHint in PGcourse.pl for course-wide attempt threshhold for revealing hints
BEGIN_PGML_HINT Recall that the integral [`\int_{C} \vec{F} \cdot d\vec{r}`] is a scalar measurement of how the vector field [`\vec{F}`] flows along the oriented curve [`C`] [$MDASH]* it is positive when it generally flows with the curve, negative when it flows against and zero when it is perpendicular or flows roughly equally with it and against it.
END_PGML_HINT
############################################################ # Solution ############################################################
BEGIN_PGML_SOLUTION In the second quadrant, the vector field flows against, and in the first quadrant it flows perpendicular to the curve. Therefore the integral is negative.
END_PGML_SOLUTION
############################################################ # End Problem ############################################################
ENDDOCUMENT();

setup.pg-code:
$integral=-2;
Context("Vector2D");
Context()->variables->are(t=>"Real");

$P = Point(2,3);
$Q = Point(0,3);
$PQ = Vector(-2,0);

$t = Formula("t");
$line = Vector("$P + $t * $PQ");

$multians = MultiAnswer( $line, Real("0"), Real("1") )->with(
  singleResult => 0,
  checker => sub {
    my ( $correct, $student, $ansHash ) = @_;
    my ( $linestu, $astu, $bstu ) = @{$student};
    my ( $linecor, $acor, $bcor ) = @{$correct};

    if ( (ParametricLine("$line") == $linestu) and
         ($linestu->eval(t=>$astu) == $line->eval(t=>"0")) and
         ($linestu->eval(t=>$bstu) == $line->eval(t=>"1")) ) 
    {  
       return [1,1,1]; 

    } elsif ( (ParametricLine("$line") == $linestu) and
              ($linestu->eval(t=>$astu) == $line->eval(t=>"0")) )
    {  
       return [1,1,0]; 

    } elsif ( (ParametricLine("$line") == $linestu) and
              ($linestu->eval(t=>$bstu) == $line->eval(t=>"1")) )
    {  
       return [1,0,1]; 

    } elsif ( (ParametricLine("$line") == $linestu) )
    {
        return [1,0,0];

    } else { 
        return [0,0,0]; 
    }

  }
);

